import React, { useState } from 'react';
import Card from './Card.jsx';
import CardData from './CardInfo.jsx';
import './index.css';


const MyApp = ()=> {
  const [cardInfo, setCardInfo]=useState(CardData);
  //console.log(cardInfo);
  const cardInfoArray = [CardInfo];
  const filterName = (category)=>{
    const updatedList = CardData.filter((item)=>{
      return item.Category===category;
    });
    setCardInfo(updatedList);
    //console.log(cardInfo);
  }
  return (
    <> 
     <nav className='navbar'>
        <div className='btn-group'>     
          <button className='btn-group_item' onClick={()=>filterName('Series')}>Series</button>
          <button className='btn-group_item' onClick={()=>filterName('Movie')}>Movies</button>
          <button className='btn-group_item' onClick={()=>filterName('Documentary')}>Documentries</button>
          <button className='btn-group_item' onClick={()=>setCardInfo(CardData)}>All</button>
        </div>
      </nav>

      <div className="heading_style">Yuva's Favorite Netflix Shows by Categories</div>
     
      <Card cardInfo={cardInfo}/>
      
    </>
  )}

export default MyApp;
